import React, {Component} from 'react'
import toastr from 'cogo-toast';
import Create from './Create'
import Edit from './Edit'

class Index extends Component
{
	constructor() {
		super();
		//--- Declare state variable for this component ---//
		this.state = {
			flights     : [
				{id : 1, aircraft_iata : "A40", airline_iata : "AC", airline_name : "SriLankan Airlines", flight_date : "2022-09-28"},
				{id : 2, aircraft_iata : "AA6", airline_iata : "UQ", airline_name : "Qatar Airways", flight_date : "2022-09-29"},
				{id : 3, aircraft_iata : "UC2", airline_iata : "JH", airline_name : "Ukraine Airlines", flight_date : "2022-09-29"}
			],
			editFlight : {}
		}
		//--- Declare method for this component ---//
		this.handleUpdateState = this.handleUpdateState.bind(this);
	}
	//--- Update state variable while any user insert or update ---//
	handleUpdateState(data, operation) {
		//--- 'operation==1' means update user ---//
		if(operation === 1) {
			this.setState(prevState => ({
				flights : prevState.flights.filter(flight => {
					if(flight.id === data.id)
						return Object.assign(flight, data);
					else
						return flight;
				})
			}))
			return;
		}
		//--- 'operation==0' means insert user ---//
		var new_flights = this.state.flights.concat(data);
		this.setState({
			flights : new_flights
		})
	}
	//--- Find editable user and update state variable ---//
	handleEditFlight(flightId) {
		this.setState({
			editFlight : this.state.flights.find(x => x.id === flightId)
		})
	}
	//--- Delete user and update state ---//
	handleDeleteFlight(id) {
		this.setState(prevState => ({
			flights : prevState.flights.filter((flight, i) => {
				return i !== id;
			})
		}))
		toastr.error('Flight has been deleted successfully!', {position : 'top-right', heading: 'Done'});
	}

    render() {
      return(
          	<div className="card mt-4">
			    <div className="card-header">
			        <h4 className="card-title"> Flights </h4>
			        <button type="button" className="btn btn-primary btn-sm pull-right" data-toggle="modal" data-target="#addModal"> Add Flight </button>
			    </div>
			    <div className="card-body">
			        <div className="col-md-12">
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th> Id </th>
                                    <th> Aircraft IATA Code </th>
                                    <th> Airline IATA Code </th>
                                    <th> Airline Name </th>
									<th> Flight Date  </th>
                                    <th> Flight Status </th>
									<th> Segment </th>
                                </tr>
                            </thead>
                            <tbody>
                            {this.state.flights.map((flight, i) => (
                                <tr key={i}>
                                    <td> {flight.id} </td>
                                    <td> {flight.aircraft_iata} </td>
                                    <td> {flight.airline_iata} </td>
                                    <td> {flight.airline_name} </td>
									<td> {flight.flight_date} </td>
                                    <td> {flight.flight_status} </td>
									<td> {flight.segments} </td>
                                    <td>
                                        <button className="btn btn-info btn-sm mr-2" onClick={this.handleEditFlight.bind(this, flight.id)} data-toggle="modal" data-target="#editModal"> Edit </button>
                                        <button className="btn btn-danger btn-sm" onClick={this.handleDeleteFlight.bind(this, i)}> Delete </button>
                                    </td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
			        </div>
			    </div>
			    <Create updateState = {this.handleUpdateState} />
			    <Edit updateState = {this.handleUpdateState} flight = {this.state.editFlight} />
			</div>
        )
    }
}
export default Index
