import React from 'react';
import {Button, Col, Container, Form, Row} from "react-bootstrap";
import axios from 'axios';
import { useState } from 'react';


function LoginPage () {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    function formsubmit () {

        let object = {
            username:username,
            password:password
        }

        axios.post("http://127.0.0.1:8084/v1/admins/login", object)
            .then(
                res => {
                    console.log(res)
                }
            )
    }

    return (
        <>
            <Container>
                <h1 className="shadow-sm text-success mt-5 p-3 text-center rounded">Admin Login</h1>
                <Row className="mt-5">
                    <Col lg={5} md={6} sm={12} className="p-5 m-auto shadow-sm rounded-lg">
                        <Form>
                            {/* <Form.Group controlId="formBasicEmail">
                                <Form.Label>User Name</Form.Label>
                                <Form.Control onChange={e=>{setUsername(e.target.value)}} type="username" placeholder="Enter Username" />
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                                <Form.Label>Password</Form.Label>
                                <Form.Control onChange={e=>{setPassword(e.target.value)}} type="password" placeholder="Password" />
                            </Form.Group>

                            <Button className='btn btn-success' variant="success btn-block" type="submit" onClick={formsubmit()}>
                                Login
                            </Button> */}
                            <form>
       <div className="input-container">
         <label>Username </label>
         <input type="text" name="uname" required />
         {renderErrorMessage("uname")}
       </div>
       <div className="input-container">
         <label>Password </label>
         <input type="password" name="pass" required />
         {renderErrorMessage("pass")}
       </div>
       <div className="button-container">
         <input type="submit" />
       </div>
     </form>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </>
    )
}

export default LoginPage;
